targets.expr:
topk(10,
  histogram_quantile(0.9, 
    sum by(chain_id, le) (
      label_replace(
        rate(envoy_cluster_upstream_rq_time_bucket{envoy_cluster_name=~"httproute/middleware/.*"}[$__rate_interval]),
        "chain_id", 
        "$1", 
        "envoy_cluster_name", 
        "httproute/middleware/([^-]+).*"
      )
    )
  )
)

topk(10,
  sum by(chain_id) (
    label_replace(
      sum by(envoy_cluster_name) (
        increase(envoy_cluster_upstream_rq{envoy_cluster_name=~"httproute/middleware/.*", envoy_response_code!~"2.."}[$__range])
      ),
      "chain_id", 
      "$1", 
      "envoy_cluster_name", 
      "httproute/middleware/([^-]+).*"
    )
  )
)
